import React, {useContext} from 'react';
import './styles.scss';
import {ThreeDots} from "react-loader-spinner";
import { CartContext } from '../../context/CartContext/CartContext';
import books from '../'

const Cart = () => {

    const { cartItems, getTotalPrice } = useContext(CartContext);

    const books = [];

    const updatedBooks = books.map((book) => {
        const cartItem = cartItems.find((item) => item.book.id === book.id);
        return {
          ...book,
          amount: cartItem ? cartItem.count : 0,
        };
      });
    return (
        books ?
            <div className='cart'>
                <div className="cart_header">
                    <button className="my-button auth_button">Purchase</button>
                </div>
                {books.length === 0 ?
                    <div>
                        Cart is empty
                    </div>
                    :
                    <div className='cart_wrapper'>
                        <div className="cart_content">
                            {
                                updatedBooks.map(book => {
                                    return (
                                        <div className="book" key={book.id}>
                                            {/* <Link className="book__name" to={`/book/${book.id}`}>
                                                {book.title}
                                            </Link> */}
                                            <span className="book_count">{book.amount === 1 ? book.amount + 'pc.': book.amount + 'pcs.'}</span>
                                            <span className="book_total-price">{book.price * book.amount}</span>
                                        </div>
                                    )
                                })
                            }
                        </div>
                        <div className="cart_footer">
                            <p className="cart_total-price">
                                <span className='title'>Total price:</span>
                                <span>{getTotalPrice()}</span>
                                totalPrice
                            </p>
                        </div>
                    </div>
                }
            </div>
        :
            <div className="loader">
                <ThreeDots
                    height="120px"
                    width="120px"
                    radius="10"
                    color="rgba(0, 0, 0, 1)"
                    ariaLabel="three-dots-loading"
                    wrapperStyle={{}}
                    wrapperClassName=""
                    visible={true}
                    className="loader"
                />
            </div>
);
}


export default Cart;
