export const booksReducer = (state, action) => {
    switch (action.type) {
      case 'ADD_BOOK':
        return [...state, action.book];
      case 'REMOVE_BOOK':
        return state.filter(book => book.id !== action.bookId);
      case 'UPDATE_BOOK':
        return state.map(book => {
          if (book.id === action.bookId) {
            return { ...book, ...action.bookUpdates };
          } else {
            return book;
          }
        });
      default:
        return state;
    }
  };
