sourcesContent:
const input = document.querySelector('input');
const incrementBtn = document.querySelector('.increment');
const decrementBtn = document.querySelector('.decrement');
const totalValue = document.querySelector('.single-book_total .value');
const priceValue = document.querySelector('.single-book_price .value').textContent;

let count = parseInt(input.value);

decrementBtn.disabled = true;


incrementBtn.addEventListener('click', () => {
  count = Math.min(42, count + 1);
  input.value = count;
  if (count == "42") {
    incrementBtn.disabled = true;
  }
  decrementBtn.disabled = false;
  updateTotalPrice();
});

decrementBtn.addEventListener('click', () => {
  count = Math.max(1, count - 1);
  input.value = count;
  if (count == "1") {
    decrementBtn.disabled = true;
  }
  incrementBtn.disabled = false;
  updateTotalPrice();
});

input.addEventListener('input', () => {
  count = parseInt(input.value);
  if (count <= 0) {
    count = 1;
    input.value = count;
  } else if (count > 42) {
    count = 42;
    input.value = count;
  }
  updateTotalPrice();
});

function updateTotalPrice() {
  const totalPrice = count * parseInt(priceValue);
  totalValue.textContent = totalPrice;
}
