import React from 'react';
import ReactDOM from "react-dom";
import './styles.css';
import {ThreeDots} from "react-loader-spinner";
import img from '../../images/avatar2.png'

const books = false

const Cart = () => {
    return (
        books ?
        <div className='cart'>
            <div className="cart__header">
                <MyButton
                    disabled={books.length === 0}
                    onClick={(e) => purchaseHandler(e)}
                >
                    Purchase
                </MyButton>
            </div>
            {books.length === 0 ?
                <div>
                    Cart is empty
                </div>
                :
                <div className='cart__wrapper'>
                    <div className="cart__content">
                        {
                            books.map(book => {
                                return (
                                    <div className="book" key={book.id}>
                                        <Link className="book__name" to={`/book/${book.id}`}>
                                            {book.title}
                                        </Link>
                                        <span className="book__count">{book.amount === 1 ? book.amount + 'pc.': book.amount + 'pcs.'}</span>
                                        <span className="book__total-price">{book.price * book.amount}</span>
                                    </div>
                                )
                            })
                        }
                    </div>
                    <div className="cart_footer">
                        <p className="cart__total-price">
                            <span className='title'>Total price:</span>
                            <span>{totalPrice}</span>
                        </p>
                    </div>
                </div>
            }
        </div>
        :
        <div className="loader">
            <ThreeDots
                height="120px"
                width="120px"
                radius="10"
                color="rgba(0, 0, 0, 1)"
                ariaLabel="three-dots-loading"
                wrapperStyle={{}}
                wrapperClassName=""
                visible={true}
                className="loader"
            />
        </div>
);
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Cart />, rootElement);


export default Cart;
