import React, { createContext, useState, useEffect } from 'react';
import books from '../../data/books.json';

export const BooksContext = createContext({
    bookslist: [],
    setBooks: () => {},
    fetchBooks: () => {}
  });

export const BooksProvider = (props) => {
  const [bookslist, setBookslist] = useState([]);

  const getBooks = async (next) => {
    try {
        let res = await fetch('books.json')
        let da = await res.json()
        const books = await da.books
        if (localStorage.getItem("books") === null) {
            localStorage.setItem('books', JSON.stringify(books))
        }
        next({type:INITIAL__BOOKS, payload:books})
    } catch (e) {
        throw e
    }
}
    const getStoragedBooks = () => {
    return JSON.parse(localStorage.getItem('books'))
}

  useEffect(() => {
    setBookslist(books);
  }, []);

  const setBooks = (books) => {
    setBookslist(books);
    localStorage.setItem('bookslist', JSON.stringify(books));
  };

  const fetchBooks = () => {
    const storedBooks = JSON.parse(localStorage.getItem('bookslist'));
    setBookslist(storedBooks);
  };

  return (
    <BooksContext.Provider value={{  bookslist, setBooks, fetchBooks }}>
      {props.children}
    </BooksContext.Provider>
  );
};
