// Fetch the books data from the JSON file
fetch('./books.json')
  .then(response => response.json())
  .then(data => {
    const books = data.books;
    const booksContent = document.querySelector('.books_content');
    let bookCatalogHTML = '';
    books.forEach(book => {
        bookCatalogHTML += `
        <div class="book-card animate_fadeInDown" id="${book.id}">
            <div class="book-card_img" id="${book.id}">
                <img src="${book.image}" alt="Default book image" onerror="this.src='../img/imageNotFound.png'">
            </div>
            <div class="book-card_content">
                <h3 class="book-card_title">${book.title}</h3>
                <h4 class="book-card_author">${book.author}</h4>
                <div class="book-card_footer">
                    <span class="book-card_price">${book.price}$</span>
                    <button id="button-${book.id}" class="my-button undefined">View</button>
                </div>
            </div>
        </div>

    `;
    booksContent.innerHTML = bookCatalogHTML;
    });
  })
  .catch(error => console.error(error));
