import React, { createContext, useState, useEffect } from 'react';
import books from '../../data/books.json';

export const BooksContext = createContext([]);

const initialState = getStoragedBooks() || [];
export const BooksProvider = (props) => {
  const [bookslist, setBookslist] = useState([]);
  const [books, booksDispatch] = useReducer(booksReducer, initialState);
    const value = {books, booksDispatch}

  useEffect(() => {
    setBookslist(books);
  }, []);

  const setBooks = (books) => {
    setBookslist(books);
    localStorage.setItem('bookslist', JSON.stringify(books));
  };

  const fetchBooks = () => {
    const storedBooks = JSON.parse(localStorage.getItem('bookslist'));
    setBookslist(storedBooks);
  };

  return (
    <BooksContext.Provider value={value}>
      {props.children}
    </BooksContext.Provider>
  );
};
