import { getUsername, deleteUsername } from "../LocalStorage/LocalStorage";
import { useNavigate } from "react-router-dom";
import "./Purchase.scss";


const Purchase = ({book, count, price}) => {

    return (
        <div className='book' id={book.id}>
            <span className='book__name'>{book.title}</span>
                <span className='book_count'>
                    {count === 1 ? count + ' pc.' : count + ' pcs.'}    
                </span>
                <span className='book_price'>{price.toFixed(2)}$</span>
            <span className='book_total-price'>{(price * count).toFixed(2)}$</span>
            <div className="book__amount-buttons">
                    <button className='decrement' data-id={book.id} onClick={decrementBookHandler}>-</button>
                    <button disabled={isError} className='increment'  data-id={book.id} onClick={incrementBookHandler}> +</button>
            </div>
            <button className='book__remove' onClick={removeHandler} data-id={book.id}>
                    <DeleteBasketSvg/>
            </button>
        </div>
    );
};

export default Purchase;
