import React, { createContext, useState } from "react";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [purchaseFlag, setPurchaseFlag] = useState(false);

  const addToCart = (cartItem) => {
    setCartItems([...cartItems, cartItem]);
  };

  const cleanCart = () => {
    setCartItems([]);
    localStorage.removeItem('cart');
    setPurchaseFlag(false);
  };

  const removeFromCart = (item) => {
    setCartItems(cartItems.filter((cartItem) => cartItem.item.id !== item.id));
  };

  const updateCartCount = (item, count) => {
    setCartItems(
      cartItems.map((cartItem) =>
        cartItem.item.id === item.id ? { item, count } : cartItem
      )
    );
  };

  const getTotalItemsCount = () => {
    return cartItems.reduce((count, cartItem) => count + cartItem.count, 0);
  };

  const getTotalPrice = () => {
    return cartItems.reduce(
      (total, cartItem) => total + cartIterm.count* 
      0
    );
  };

  const contextValues = {
    cartItems,
    cleanCart,
    addToCart,
    removeFromCart,
    updateCartCount,
    getTotalItemsCount,
    getTotalPrice,
    purchaseFlag, // Add the purchaseFlag to the context values
    setPurchaseFlag, // Add the setPurchaseFlag function to the context values
  };

  return (
    <CartContext.Provider value={contextValues}>
      {children}
    </CartContext.Provider>
  );
};
