import React, { useContext } from 'react';
import './styles.scss';
import { ThreeDots } from 'react-loader-spinner';
import { CartContext } from '../../context/CartContext/CartContext';

const Cart = () => {
  const { cartItems, getTotalPrice } = useContext(CartContext);

  return (
    <div className='cart'>
      <div className='cart_header'>
        <button className='my-button auth_button'>Purchase</button>
      </div>
      {cartItems.length === 0 ? (
        <div>Cart is empty</div>
      ) : (
        <div className='cart_wrapper'>
          <div className='cart_content'>
            {cartItems.map((cartItem) => {
              const { book, count } = cartItem;
              const pricePerUnit = book.price / count;

              return (
                <div className='book' key={book.id}>
                  <span className='book__name'>{book.title}</span>
                  <span className='book_count'>
                    {count === 1 ? count + ' pc.' : count + ' pcs.'}
                  </span>
                  <span className='book_price'>{pricePerUnit}$</span>
                  <span className='book_total-price'>{pricePerUnit * count}</span>
                </div>
              );
            })}
          </div>
          <div className='cart_footer'>
            <p className='cart_total-price'>
              <span className='title'>Total price:</span>
              <span>{getTotalPrice()}</span>
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
