import React, { createContext, useState, useEffect } from 'react';
import books from '../../data/books.json';

export const BooksContext = createContext([]);

export const BooksProvider = (props) => {
  const [bookslist, setBookslist] = useState([]);

  useEffect(() => {
    setBookslist(books);
  }, []);

  const setBook = (book) => {
    const newBooksList = [...bookslist, book];
    setBookslist(newBooksList);
    localStorage.setItem('bookslist', JSON.stringify(newBooksList));
  };

  const fetchBooks = () => {
    const storedBooks = JSON.parse(localStorage.getItem('bookslist'));
    if (storedBooks) {
      setBookslist(storedBooks);
    } else {
      setBookslist(books);
    }
  };

  return (
    <BooksContext.Provider value={{ bookslist }}>
      {props.children}
    </BooksContext.Provider>
  );
};
